import React, { Component } from 'react'
import axios from 'axios';
import '../css/userhome.css';
import gamezone from '../images/gamezone.gif';
export default class UserHome extends Component {
    constructor(props){
        super(props);
        window.localStorage.setItem("User Action" , true);
        this.state = {
            ComingSoonGames : [],
            OnGoingGames:[],
            FinishedGames:[]
        };
    }
    componentDidMount(){
        const now = new Date();
        axios.get('http://localhost:5000/game/',{
            withCredentials:true
        })
        .then( res => {
            if(res.data.games){
                for (const iterator of res.data.games) {
                    console.log(Date(iterator.endTime));
                    console.log(now);
                    if(Date(iterator.startTime) > now){
                        this.state.ComingSoonGames.push(iterator);
                    }
                    if(Date(iterator.endTime) < now){
                        this.state.FinishedGames.push(iterator);
                    }
                    if(Date(iterator.startTime) === now){
                        this.state.OnGoingGames.push(iterator);
                    }
                }
            }
            console.log(this.state.FinishedGames);
            window.localStorage.setItem('sessionID' , res.data.sessionID);
        });

    }
    render() {
        return (
            <div className="user-home">
                <div className="games-screen">
                    <div className="banner">
                        <img src={gamezone} alt=""/>
                    </div>
                    <div className="coming-soon">
                        game1
                    </div>
                </div>
            </div>
        )
    }
}
