import React, { Component } from 'react'
import '../css/gamescreen.css';
import {Button} from 'react-bootstrap';
import {Banner} from './userhome';
import backspace from '../images/ic-backspace@3x.png';
import { CountdownCircleTimer } from 'react-countdown-circle-timer'
import Timer from 'react-compound-timer';
export default class GameScreen extends Component {
    constructor(props){
        super(props);
        this.state = {
            timer : true,
            game : this.props.location.state.game,
            id : 0
        };
        this.handleBackSpace = this.handleBackSpace.bind(this);
        this.handleAnswered = this.handleAnswered.bind(this);
    }
    handleBackSpace(e){
        e.preventDefault();
        this.props.history.goBack();
    }
    handleAnswered(e){
        if((this.state.id+1) < this.state.game.questions.length){
            console.log(this.state.id+' '+this.state.game.questions.length);
            var currentQ = this.state.id;
            currentQ++;
            this.setState({
                id: currentQ
            });
        }
    }
    componentDidMount(){
        setTimeout(() => {
            this.setState({
                timer : false
            });
        }, 5000);
    }
    render() {
        return (
            <div className="gameScreen">
                {this.state.timer ? 
                    <div className="white-screen">
                        <div className="inner-counter">
                        <RoundCountdown/> 
                        </div>
                    </div>
                    :
                    <div className="inner-gameScreen">
                    <Banner/>
                    <div className="back-button" onClick={this.handleBackSpace}>
                        <img src={backspace} alt="back"/>
                    </div>
                    <div className="game-main">
                        <GameArea game={this.state.game} id={this.state.id} handleAnswered={this.handleAnswered}/>
                    </div>
                </div>
                }
            </div>
        )
    }
}


export function RoundCountdown(props){
    return(
            <CountdownCircleTimer
            isPlaying
            duration={5}
            colors={[["#D14081"]]}
            >
                {({ remainingTime }) =>  <span>Game will start on <br/>{remainingTime}<br/>seconds</span>}
            </CountdownCircleTimer>
    )
}

export function GameArea(props){ 
    const id= props.id;
    const game = props.game;
    console.log(game.questions);
    return(
        <div className="block">
            <div className="block-topper">
               <div className="que-num">
               Question {id+1} of {game.questions.length}
               </div> 
            <div className="question-timer">
                <Timer
                  initialTime={20000}
                  direction="backward"  
                >
                    {() => (
                        <React.Fragment>
                            <Timer.Seconds />s left
                        </React.Fragment>
                    )}
                </Timer>
            </div>
            </div>
            <Question question={game.questions[id].question}/>
        </div>
    )
}

export function Question(props){
    const question = props.question;
    console.log(question)
    return(
        <div>
            hello
        </div>
    )
}
