import React, { Component } from 'react'
import '../css/gamescreen.css';
import {Banner} from './userhome';
import backspace from '../images/ic-backspace@3x.png';
import { CountdownCircleTimer } from 'react-countdown-circle-timer'

export default class GameScreen extends Component {
    constructor(props){
        super(props);
        console.log(this.props.location.state.game);
        this.handleBackSpace = this.handleBackSpace.bind(this);
    }
    handleBackSpace(e){
        e.preventDefault();
        this.props.history.goBack();
    }

    render() {
        return (
            <div className="gameScreen">
                <Countdown/>
                <div className="inner-gameScreen">
                    <Banner/>
                    <div className="back-button" onClick={this.handleBackSpace}>
                        <img src={backspace} alt="back"/>
                    </div>
                </div>
            </div>
        )
    }
}


export function Countdown(props){
    return(
        <CountdownCircleTimer
        isPlaying
        duration={10}
        colors={[
        ['#004777', 0.33],
        ['#F7B801', 0.33],
        ['#A30000', 0.33],
        ]}
        >
        </CountdownCircleTimer>
    )
}
