import React, { Component } from 'react'
import '../css/winner.css';
import history from '../history';
import { Button, FormGroup, FormControl, ControlLabel, HelpBlock} from "react-bootstrap";
import {Banner} from './userhome';
import bg from '../images/confetti-3-x.png';
export default class Winner extends Component {
    constructor(props){
        super(props);
        this.state = {
            email:''
        };
        this.handleChangeEmail = this.handleChangeEmail.bind(this);
        this.handleSubmit=this.handleSubmit.bind(this);
    }
    handleChangeEmail(e){
        e.preventDefault();
        this.setState({
            email : e.target.value,
            err:''
        });
    }
    handleSubmit(e){
        e.preventDefault();
        var emailformat = /[a-z0-9]+@[a-z].[a-z]/;
        if(emailformat.test(this.state.email)){
            console.log('success');
        }
        else{
            this.setState({
                err:'enter a valid email!'
            })
        }
    }
    componentWillMount(){
        if(this.props.location.state === undefined){
            window.location.href ="/";
        }   
    }
    render() {
        return (
            <div className="winner">
                <Banner/>
                <div className="inner-winner-block">
                    <div className="inner-winner">
                    <img src={bg} alt=""/>
                    <p>Winner!</p>
                    </div>
                    <div id="form">
                    <form>
                    <HelpBlock>
                    <p className="text-danger">{this.state.err}</p>
                    </HelpBlock>
                    <FormGroup controlId="email" bsSize="large">
                    <ControlLabel>Email</ControlLabel>
                    <FormControl
                        autoFocus
                        type="email"
                        value={this.state.email}
                        onChange={e => this.handleChangeEmail(e)}
                    />
                    </FormGroup>
                    <div id="submit">
                    <Button  type="button" onClick={this.handleSubmit}>
                        Submit
                    </Button>
                    </div>
                    </form>               
                    </div>
                    </div>
            </div>
        )
    }
}
