import React, { Component } from 'react';
import { Button, FormGroup, FormControl, ControlLabel , HelpBlock} from "react-bootstrap";
import banner from '../images/banner.jpg';
import { Link } from 'react-router-dom';
import '../css/login.css'

export default class Signup extends Component{
    constructor(props){
        super(props);
        this.state = {
            email:'',
            password:'',
            username:''
        }
        this.handleSubmit = this.handleSubmit.bind(this);
        this.setEmail = this.setEmail.bind(this);
        this.setUsername = this.setUsername.bind(this);
        this.setPassword = this.setPassword.bind(this);
    }
    setEmail(value){
        this.setState({email: value});
    }
    setPassword(value){
        this.setState({password: value});
    }
    setUsername(value){
        this.setState({username: value});
    }
    validateForm() {
        return this.state.email.length > 6 && this.state.password.length > 6;
      }
    handleSubmit(e){
        e.preventDefault();
        console.log(this.state);
    }
    render(){
        return(
            <div className="Login">
                <div className="banner">
                    <img src={banner} alt=""></img>
                </div>
                <form onSubmit={this.handleSubmit}>
                   <FormGroup controlId="username" bsSize="large">
                    <ControlLabel>Username</ControlLabel>
                    <FormControl
                        autoFocus
                        type="username"
                        value={this.state.username}
                        onChange={e =>this.setUsername(e.target.value)}

                    />
                    </FormGroup>
                    <FormGroup controlId="email" bsSize="large">
                    <ControlLabel>Email</ControlLabel>
                    <FormControl
                        autoFocus
                        type="email"
                        value={this.state.email}
                        onChange={e =>this.setEmail(e.target.value)}
                    />
                    <HelpBlock>
                    <p className="text-danger">{this.state.EmailErr}</p>
                    </HelpBlock>
                    </FormGroup>
                    <FormGroup controlId="password" bsSize="large">
                    <ControlLabel>Password</ControlLabel>
                    <FormControl
                        value={this.state.password}
                        onChange={e => this.setPassword(e.target.value)}
                        type="password"
                    />

                    </FormGroup>
                    <Button id="login-button" block bsSize="large" disabled={!this.validateForm()} type="submit">
                    Login
                    </Button>
                    <div className="signup">
                    <Link to="/">Already a user? Log in!</Link>
                    </div>
                </form>
            </div>
        )
    }
}
