import React, { Component } from 'react'
import '../css/gamescreen.css';
import {Button} from 'react-bootstrap';
import {Banner} from './userhome';
import backspace from '../images/ic-backspace@3x.png';
import { CountdownCircleTimer } from 'react-countdown-circle-timer'

export default class GameScreen extends Component {
    constructor(props){
        super(props);
        this.state = {
            timer : true,
            game : this.props.location.state.game,
            id : 0
        };
        this.handleBackSpace = this.handleBackSpace.bind(this);
        this.handleAnswered = this.handleAnswered.bind(this);
    }
    handleBackSpace(e){
        e.preventDefault();
        this.props.history.goBack();
    }
    handleAnswered(e){
        var currentQ = this.state.id;
        currentQ++;
        this.setState({
            id: currentQ
        });
    }
    componentDidMount(){
        setTimeout(() => {
            this.setState({
                timer : false
            });
        }, 5000);
    }
    render() {
        return (
            <div className="gameScreen">
                {this.state.timer ? 
                    <div className="white-screen">
                        <div className="inner-counter">
                        <Countdown/> 
                        </div>
                    </div>
                    :
                    <div className="inner-gameScreen">
                    <Banner/>
                    <div className="back-button" onClick={this.handleBackSpace}>
                        <img src={backspace} alt="back"/>
                    </div>
                    <div className="game-main">
                        <GameArea game={this.state.game} id={this.state.id} handleAnswered={this.handleAnswered}/>
                    </div>
                </div>
                }
            </div>
        )
    }
}


export function Countdown(props){
    return(
            <CountdownCircleTimer
            isPlaying
            duration={5}
            colors={[["#D14081"]]}
            >
                {({ remainingTime }) =>  <span>Game will start on <br/>{remainingTime}<br/>seconds</span>}
            </CountdownCircleTimer>
    )
}

export function GameArea(props){ 
    const id= props.id;
    const game = props.game;
    console.log(game.questions);
    return(
        <div className="block">
            <div className="block-topper">
                Question
            </div>
            <Button className="bg-primary" onClick={props.handleAnswered} type="button">correct</Button>
        </div>
    )
}
