import React, { Component } from 'react'
import '../css/adminGames.css';
import history from '../history';
import axios from 'axios';
export default class AdminGames extends Component {
    constructor(props){
        super(props);
        this.state = {
            username : this.props.location.state.username,
            games : Array
        };
    }
    componentDidMount(){
        if(this.props.location.state === undefined){
            history.push('/admin');
        }
        const username = this.props.location.state.username;
        axios.post('http://localhost:5000/game/adminGames',{username})
        .then( res => {
            this.setState({
                games: res.data
            });
        });

    }
    render() {
        return (
            <div className="panel">
                <div className="inner-panel">
                <h4 style={{textAlign:"center"}}>Your Games</h4>
                </div>
                <GameBlock games={this.state.games}/>
            </div>
        )
    }
}

export function GameBlock(props){
    const games = props.games;
    console.log(games);
    const gamesList = [];
    games.forEach((game,index) => {
        gamesList.push(
        <div id="game" key={index}>{game.gamaName}</div>
        )
    });
    return(
        {gamesList}
    )
}
