import React, { Component } from 'react'
import { Button, FormGroup, FormControl, ControlLabel } from "react-bootstrap";
export default class Home extends Component {
    constructor(props){
        super(props);
        this.gameImage = React.createRef();
        this.state = {
            gameName:'',
            startTime: new Date(),
            endTime:new Date(),
            questions:[],
            que:{
                question:'',
                answer:'',
                image:'',
                suffledAnswer:''
            },
            steps:[],
            gameBgColor:'',
            gameCreator:''
        };
        this.handlechangeGameName = this.handlechangeGameName.bind(this);
        this.handlechangeGameImage = this.handlechangeGameImage.bind(this);
        this.handlechangeStartTime = this.handlechangeStartTime.bind(this);
        this.handlechangeEndTime = this.handlechangeEndTime.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }
    handlechangeGameName(value){
        this.setState({
            gameName: value
        })
    }
    handlechangeGameImage(){
        console.log(this.gameImage.current.value);
    }
    handlechangeStartTime(value){
        this.setState({
            startTime: value
        })
    }
    handlechangeEndTime(value){
        this.setState({
            endTime: value
        })
    }
    handleSubmit(e){
        e.preventDefault();
    }
    render() {
        return (
            <div className="home">
                 <h3>Add a new Game</h3>
                <form onSubmit={this.handleSubmit}>
                   <FormGroup controlId="gamename" bsSize="large">
                    <ControlLabel>Gamename</ControlLabel>
                    <FormControl
                        autoFocus
                        type="text"
                        value={this.state.gameName}
                        onChange={e =>this.handlechangeGameName(e.target.value)}

                    />
                    </FormGroup>
                    <FormGroup controlId="gameimage" bsSize="large">
                    <ControlLabel>Game Image</ControlLabel>
                    <FormControl
                        autoFocus
                        type="file"
                        value={this.state.gameImage}
                        onChange={this.handlechangeGameImage()}
                        ref={this.gameImage}
                    />
                    </FormGroup>
                    <FormGroup controlId="starttime" bsSize="large">
                    <ControlLabel>startTime</ControlLabel>

                    </FormGroup>
                    <FormGroup controlId="endtime" bsSize="large">
                    <ControlLabel>endTime</ControlLabel>

                    </FormGroup>
                    <div className="submit">
                    <Button id="login-button" bsSize="small" type="submit">
                        Add
                    </Button><br/>
                    </div>
                </form>
            </div>
        )
    }
}
