import React, { Component } from 'react'
import '../css/adminGames.css';
import history from '../history';
import axios from 'axios';
import BeatLoader from "react-spinners/ClipLoader";
import {Button} from 'react-bootstrap';
export default class AdminGames extends Component {
    constructor(props){
        super(props);
        this.state = {
            username : this.props.location.state.username,
            games : []
        };
        const username = this.state.username;
        axios.post('http://localhost:5000/game/adminGames',{username})
        .then( res => {
            this.setState({
                games: res.data
            });
            console.log(this.state.games);
        });
        this.handlePreview = this.handlePreview.bind(this);
    }
    handlePreview(game){
        console.log(game);
    }
    componentDidMount(){
        if(this.props.location.state === undefined){
            history.push('/admin');
        }
    }
    render() {
        return (
            <div className="panel">
                <div className="inner-panel">
                <h4 style={{textAlign:"center"}}>Your Games</h4>
                {this.state.games.length > 0 ?
                <GameBlock games={this.state.games} handlePreview={this.handlePreview}/>:
                <div>
                    <BeatLoader
                    size={50}
                    color={"white"}
                    loading={!this.state.isLoaded}
                    />
                </div>
                }
                </div>
            </div>
        )
    }
}

export function GameBlock(props){
    const games = props.games;
    console.log(games);
    const gamesList = [];
    games.forEach((game,index) => {
        gamesList.push(
        <div id="game" key={index}>
            <div id="topper">
            {game.gameName}
            <Button id="preview-butt" type="button" onClick={(e) => {props.handlePreview(game)}}>Preview</Button>
            </div>
            <br/>
            {
            game.winners.length > 0?
            <div id="winners">
                <h5>Winners</h5>
                <Winners winners={game.winners}/>
            </div>:
            <div></div>
            }   
        </div>
        )
    });
    return(
        <div className="games">
            {gamesList}
        </div>
    )
}

export function Winners(props){
    const winners = props.winners;
    const winnersList = [];
    winners.forEach(winner => {
        winnersList.push(
        <li key={winner}>{winner}</li>
        );
    });
    return(
        <ul>
            {winnersList}
        </ul>
    )
}
